<template>
  <div id="home">
    <div class="card" id="weatherCard">
      <div class="loading hide" :class="{show: isActive}"></div>
      <div class="wrap-content" :class="{hide: isActive}">
        <div class="wrap wrap-place">
          <p class="city">{{ location.city }}</p>
          <p class="state">{{ location.country }}, {{ location.region }}</p>
        </div>
        <div class="wrap wrap-condition">
          <p class="condition">{{ condition.text }}</p>
          <div class="icon">
            <img :src="dataIcon" />
          </div>
        </div>
        <div class="wrap wrap-temp">
          <p class="temp">{{ condition.temp }}°</p>
          <p class="vtemp">{{ forecast.high }}° / {{ forecast.low }}°</p>
        </div>
