		var self = this;
		var place = "istanbul";
		self.$http.get('https://query.yahooapis.com/v1/public/yql?q=select * from weather.forecast where woeid in (select woeid from geo.places(1) where text="'+ place +'") and u="c"&format=json').then(function(response){
			console.log(response);

			if(response.ok){
				self.isActive = false;

				var weather = response.data.query.results.channel;
				self.location = weather.location;
				self.condition = weather.item.condition;
				self.forecast = weather.item.forecast["0"];

				icons = {
					// tornado
					0: "data:image/svg+xml;utf8;base64,",
					// tropical storm
					1: "data:image/svg+xml;utf8;base64,",
					// hurricane
					2:	"data:image/svg+xml;utf8;base64,",
