					// light snow showers
					14: "data:image/svg+xml;utf8;base64,",
					// blowing snow
					15: "data:image/svg+xml;utf8;base64,",
					//snow
					16:	"data:image/svg+xml;utf8;base64,",
					// hail
					17:	"data:image/svg+xml;utf8;base64,",
					// sleet
					18:	"data:image/svg+xml;utf8;base64,",
					// dust
					19:	"data:image/svg+xml;utf8;base64,",
					// foggy
					20:	"data:image/svg+xml;utf8;base64,",
					// haze
					21:	"data:image/svg+xml;utf8;base64,",
					// smoky
					22:	"data:image/svg+xml;utf8;base64,",
					// blustery
					23:	"data:image/svg+xml;utf8;base64,",
					// windy
					24:	"data:image/svg+xml;utf8;base64,",
					// cold
					25:	"data:image/svg+xml;utf8;base64,",
					// cloudy
					26:	"data:image/svg+xml;utf8;base64,",
					// mostly cloudy (night)
					27:	"data:image/svg+xml;utf8;base64,",
					// mostly cloudy (day)
					28: "data:image/svg+xml;utf8;base64,",
					// partly cloudy (night)
					29:	"data:image/svg+xml;utf8;base64,",
					// partly cloudy (day)
					30:	"data:image/svg+xml;utf8;base64,",
					// clear (night)
					31:	"data:image/svg+xml;utf8;base64,",
					// sunny
					32:	"data:image/svg+xml;utf8;base64,",
					// fair (night)
					33:	"data:image/svg+xml;utf8;base64,",
					// fair (day)
					34:	"data:image/svg+xml;utf8;base64,",
					// mixed rain and hail
					35:	"data:image/svg+xml;utf8;base64,",
					// hot
					36:	"data:image/svg+xml;utf8;base64,",
					// isolated thunderstorms
					37:	"data:image/svg+xml;utf8;base64,",
					// scattered thunderstorms
					38:	"data:image/svg+xml;utf8;base64,",
					// scattered thunderstorms
					39:	"data:image/svg+xml;utf8;base64,",
					//scattered showers
					40:	"data:image/svg+xml;utf8;base64,",
					// heavy snow
					41:	"data:image/svg+xml;utf8;base64,",
					// scattered snow showers
					42:	"data:image/svg+xml;utf8;base64,",
					// heavy snow
					43:	"data:image/svg+xml;utf8;base64,",
					// partly cloudy
					44:	"data:image/svg+xml;utf8;base64,",
					// thundershowers
					45:	"data:image/svg+xml;utf8;base64,",
					// snow showers
					46:	"data:image/svg+xml;utf8;base64,",
					// isolated thundershowers
					47:	"data:image/svg+xml;utf8;base64,",
					// not available
					3200: "data:image/svg+xml;utf8;base64,",
				};

				var code = weather.item.condition.code;
				self.dataIcon = icons[code];

			}else{
				self.isActive = true;
			}
		});
	}
  }

// var icons icons added to tab
// TODO also correctly located and more fit to screen
// or it may be in login screen under username & password buttons
